	const [notes, setNotes] = useState([]);

	const onAddNote = () => {
		const newNote = {
      id: uuid(),
      title: "Untitled Note",
      body: "",
      lastModified: Date.now()
    };
	};

	return (
		<div className='App'>
			<Sidebar notes={notes} onAddNote={onAddNote} />
			<Main />
		</div>
	);
}

export default App;
